shader.vertex:
attribute vec3 Position;
attribute vec4 Color;
attribute vec2 TexCoord;
attribute vec4 RenderData;
attribute float Scale;

attribute float Alpha;

varying float AlphaOut;
varying vec4 Color0;
varying vec2 TexCoord0;

uniform mat4 Transform;
void main(void)
{
    AlphaOut = Alpha;
    Color0 = Color;
    TexCoord0 = TexCoord;
    gl_Position = Transform * vec4(Position.xyz, 1.0);
}

shader.fragment:
varying lowp vec4 Color0;
varying mediump vec2 TexCoord0;
varying lowp float AlphaOut;
uniform sampler2D Texture0;
void main(void)
{
    vec4 Color = Color0 * texture2D(Texture0, TexCoord0);
    float a = clamp(AlphaOut, 0, 1);
    Color.r += a;
    Color.g += a;
    Color.b += a;
    gl_FragColor = Color;
}

shader.vertex:
attribute vec3 Position;
attribute vec4 Color;
attribute vec2 TexCoord;
attribute vec4 RenderData;
attribute float Scale;

attribute float Alpha;

varying float AlphaOut;
varying vec4 Color0;
varying vec2 TexCoord0;

uniform mat4 Transform;
void main(void)
{
    AlphaOut = Alpha;
    Color0 = Color;
    TexCoord0 = TexCoord;
    gl_Position = Transform * vec4(Position.xyz, 1.0);
}

shader.fragment:
varying lowp vec4 Color0;
varying mediump vec2 TexCoord0;
varying lowp float AlphaOut;
uniform sampler2D Texture0;
void main(void)
{
    vec4 Color = Color0 * texture2D(Texture0, TexCoord0);
    float a = clamp(AlphaOut, 0, 1);
    Color.r *= 1-a;
    Color.g *= 1-a;
    Color.b *= 1-a;
    gl_FragColor = Color;
}

shader.vertex:
attribute vec3 Position;
attribute vec4 Color;
attribute vec2 TexCoord;
attribute vec4 RenderData;
attribute float Scale;

varying vec4 Color0;
varying vec2 TexCoord0;

uniform mat4 Transform;
void main(void)
{
    Color0 = Color;
    TexCoord0 = TexCoord;
    gl_Position = Transform * vec4(Position.xyz, 1.0);
}

shader.fragment:
varying lowp vec4 Color0;
varying mediump vec2 TexCoord0;
uniform sampler2D Texture0;
void main(void)
{
    vec4 Color = Color0 * texture2D(Texture0, TexCoord0);
    gl_FragColor = Color;
}
